mapper.insert:
insert into attend 
	(at_date
	, emp_no
	, at_type
	, at_week)
values (sysdate
	, #{empNo}
	, '기본'
	, #{atWeek}
)

mapper.update:
update attend
set at_type= #{atType}
	where emp_no = #{empNo}
and TO_DATE(at_date, 'YY/MM/DD') = TO_DATE(#{atDate, javaType=java.sql.Date, jdbcType=DATE}, 'YY/MM/DD')

mapper.select:
		select at_date, emp_no, at_start, at_end, at_overtime, at_type, at_result, at_week
		from attend
		where emp_no = #{empNo}
		and to_char(at_date,'YYYYMM') = to_char(sysdate,'YYYYMM')
  		and at_week = #{atWeek}

select *
from attend	
where to_char(at_date, 'YYYY-MM') = #{yearMonth}
and emp_no = #{empNo}

and at_date < to_char(sysdate,'yy/MM/dd')

order by at_date

select at_week
from attend	
where to_char(at_date, 'YYYY-MM') = #{yearMonth}
and emp_no = #{empNo}
group by at_week
order by at_week

select *
from attend
where to_char(at_date, 'YYYY-MM') = #{yearMonth}
and emp_no = #{empNo}

and at_date < to_char(sysdate,'yy/MM/dd')


	and at_week = #{atWeek}

mapper.update:
update atstatus
set at_status = #{atStatus}
where emp_no = #{empNo}

mapper.select:
select *
from atstatus
where emp_no = #{empNo}

select to_char(ayann_dt, 'YYYY') AYANN_DT, ayann_cnt, ayann_rest
from ayann
where emp_no = #{empNo} and to_char(ayann_dt, 'YYYY') = #{ayannDt}

