mapper.insert:

	select fevent_sequence.nextval from dual

insert into FEVENT (
	FE_NO, FE_TITLE, FE_CONTENT, FE_DATE, FE_HIT, FEWRITER_EMPNO
) VALUES (
	#{feNo}, #{feTitle}, #{feContent}, sysdate, 0 ,#{feWriterEmpNo}
)

mapper.select:
SELECT emp_photo
from emp
where emp_no = #{empNo}

mapper.update:
UPDATE fevent
SET 
	FE_TITLE = #{feTitle},
    FE_CONTENT = #{feContent}
WHERE FE_NO = #{feNo}
