mapper.insert:
INSERT INTO ASRENT (
	ASR_CD, AS_CD, ASR_DATE, ASR_STARTTIME, ASR_ENDTIME, ASR_REASON, EMP_NO
) VALUES (
	(SELECT NVL(MAX(TO_NUMBER(ASR_CD)), 0) + 1 FROM ASRENT), #{asCd}, sysdate, #{asrStarttime}, #{asrEndtime}, #{asrReason, jdbcType=VARCHAR}, #{empNo}
)

mapper.select:
select
    b.*
from(
    select
        a.*, row_number() over (order by a.asr_cd desc) rnum
    from(
        SELECT
            asr_cd, asset.as_se, asset.as_cd, asr_date, asr_starttime, asr_endtime, asr_reason, emp_no, asset.as_name
        FROM
            asrent
        INNER JOIN asset on asrent.as_cd = asset.as_cd
        WHERE
            emp_no = #{empNo}
            
        ORDER By asr_date desc
    )a
)b

	where b.rnum >= #{startRow} and b.rnum <= #{endRow}
	ORDER By asr_date desc

mapper.delete:
DELETE FROM asrent
WHERE
    	as_cd = #{asCd}
    AND to_char(asr_date,'yy/MM/dd') = to_char(sysdate,'yy/MM/dd')
    AND emp_no = #{empNo}
