mapper.update:
UPDATE CHATMEMBER
SET CC_END = (
    SELECT MAX(CHAT_NO)
    FROM CHAT
    WHERE CR_NO = #{crNo}
)
WHERE CR_NO = #{crNo}
  AND EMP_NO = #{empNo}

