mapper.select:
select b.* from( select a.*, row_number() over (order by a.rr_cd desc) rnum from( SELECT RR_CD ,roomres.ROOM_CD ,RR_DATE ,RR_STARTTIME ,RR_ENDTIME ,RR_REASON ,EMP_NO ,room.ROOM_NAME ,room.ROOM_LOC FROM roomres INNER JOIN room on roomres.room_cd = room.room_cd WHERE emp_no = #{empNo}
ORDER BY rr_date desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow} ORDER BY rr_date desc

mapper.delete:
DELETE FROM roomres
WHERE
    	room_cd = #{roomCd}
    AND to_char(rr_date,'yy/MM/dd') = to_char(sysdate,'yy/MM/dd')
    AND emp_no = #{empNo}
